using System;
using System.Collections.Generic;
using System.Reflection;
using Microsoft.AspNetCore.Mvc.Rendering;

namespace Mollie.Sample.Framework.Extensions {
    /// <summary>
    /// Class HtmlHelperExtensions.
    /// </summary>
    /// <autogeneratedoc />
    public static class HtmlHelperExtensions {
        /// <summary>
        /// Gets the static string select list.
        /// </summary>
        /// <param name="htmlHelper">The HTML helper.</param>
        /// <param name="type">The type.</param>
        /// <returns>IEnumerable&lt;SelectListItem&gt;.</returns>
        /// <autogeneratedoc />
        public static IEnumerable<SelectListItem> GetStaticStringSelectList(this IHtmlHelper htmlHelper, Type type) {
            foreach (var fieldInfo in type.GetFields(BindingFlags.Static | BindingFlags.Public)) {
                var value = fieldInfo.GetValue(null).ToString();

                yield return new SelectListItem() {
                    Value = value,
                    Text = value
                };
            }
        }
    }
}
