using System.Threading.Tasks;
using ISynergy.Framework.Payment.Mollie.Models;
using Microsoft.AspNetCore.Mvc;
using Mollie.Sample.Models;
using Mollie.Sample.Services.Payment;

namespace Mollie.Sample.Controllers
{
    /// <summary>
    /// Class PaymentController.
    /// Implements the <see cref="Controller" />
    /// </summary>
    /// <seealso cref="Controller" />
    /// <autogeneratedoc />
    public class PaymentController : Controller {
        /// <summary>
        /// The payment overview client
        /// </summary>
        /// <autogeneratedoc />
        private readonly IPaymentOverviewClient _paymentOverviewClient;
        /// <summary>
        /// The payment storage client
        /// </summary>
        /// <autogeneratedoc />
        private readonly IPaymentStorageClient _paymentStorageClient;

        /// <summary>
        /// Initializes a new instance of the <see cref="PaymentController"/> class.
        /// </summary>
        /// <param name="paymentOverviewClient">The payment overview client.</param>
        /// <param name="paymentStorageClient">The payment storage client.</param>
        /// <autogeneratedoc />
        public PaymentController(IPaymentOverviewClient paymentOverviewClient, IPaymentStorageClient paymentStorageClient) {
            _paymentOverviewClient = paymentOverviewClient;
            _paymentStorageClient = paymentStorageClient;
        }

        /// <summary>
        /// Indexes this instance.
        /// </summary>
        /// <returns>ViewResult.</returns>
        /// <autogeneratedoc />
        [HttpGet]
        public async Task<ViewResult> Index() {
            var model = await _paymentOverviewClient.GetList();
            return View(model);
        }

        /// <summary>
        /// APIs the URL.
        /// </summary>
        /// <param name="url">The URL.</param>
        /// <returns>ViewResult.</returns>
        /// <autogeneratedoc />
        [HttpGet]
        public async Task<ViewResult> ApiUrl([FromQuery]string url) {
            var model = await _paymentOverviewClient.GetListByUrl(url);
            return View(nameof(this.Index), model);
        }

        /// <summary>
        /// Creates this instance.
        /// </summary>
        /// <returns>ViewResult.</returns>
        /// <autogeneratedoc />
        [HttpGet]
        public ViewResult Create() {
            var model = new CreatePaymentModel() {
                Amount = 10,
                Currency = Currency.EUR
            };

            return View(model);
        }

        /// <summary>
        /// Creates the specified model.
        /// </summary>
        /// <param name="model">The model.</param>
        /// <returns>IActionResult.</returns>
        /// <autogeneratedoc />
        [HttpPost]
        public async Task<IActionResult> Create(CreatePaymentModel model) {
            if (!ModelState.IsValid) {
                return View();
            }

            await _paymentStorageClient.Create(model);
            return RedirectToAction(nameof(this.Index));
        }
    }
}
